/**
 * creates a new tab
 * @param url the url of new tab
**/
function newTab(url) {
  chrome.tabs.create(url)
}

/**
 * creates a new tab manager
**/
class tabManager {
  tabId = null
  async construct(queryOptions) {
    let [tId] = chrome.tabs.query(queryOptions)

    this.tabId = tId
  }
  /**
   * close the tab
  **/
  close = () => {
    chrome.tabs.remove(this.tabId)
  }
  /**
   * do something
   * @param action whats will do
  **/
  Do = (action) => {
    if (action == "close" || action == "bm:close") {
      chrome.tabs.remove(this.tabId)
    } else if (action == "refresh" || action == "bm:refresh") {}
  }
}
